'        END IF;',
'    EXCEPTION',
'        WHEN CURSOR_ALREADY_OPEN THEN',
'            CLOSE v_result_cursor;',
'            RAISE;',
'        WHEN INVALID_CURSOR THEN',
'            CLOSE v_result_cursor;',
'            RAISE;',
'    END;',
'',
'    RETURN v_result_cursor;',
'    END ZnajdzFilmyZDanaRecenzja;',
