unistr('            RAISE_APPLICATION_ERROR(-20001, ''B\0142\0105d przy liczeniu ilo\015Bci wypo\017Cycze\0144.'');'),
'        ',
'    END;',
'',
'    RETURN v_ilosc_wypozyczen;',
